import React, { useState } from "react"
import Authors from "./components/Authors"
import Books from "./components/Books"
import NewBook from "./components/NewBook"
import Login from "./components/Login"
import Notification from "./components/Notification"
import { gql } from "apollo-boost"
import { Mutation, Query, Subscription } from "react-apollo"
import {
  useQuery,
  useMutation,
  useSubscription,
  useApolloClient
} from "@apollo/react-hooks"

const CREATE_BOOK = gql`
  mutation addBook(
    $title: String!
    $published: Int!
    $author: String!
    $genres: [String!]!
  ) {
    addBook(
      title: $title
      author: $author
      published: $published
      genres: $genres
    ) {
      title
      published
      author {
        name
      }
    }
  }
`

const LOGIN = gql`
  mutation login($username: String!, $password: String!) {
    login(username: $username, password: $password) {
      value
    }
  }
`

const ALL_AUTHORS = gql`
  {
    allAuthors {
      name
      born
      bookCount
    }
  }
`

const ALL_BOOKS = gql`
  {
    allBooks {
      title
      published
      genres
      author {
        name
      }
    }
  }
`

const ME = gql`
  {
    me {
      username
      favoriteGenre
    }
  }
`

const BOOK_ADDED = gql`
  subscription {
    bookAdded {
      title
      author {
        name
      }
    }
  }
`

const App = () => {
  const [page, setPage] = useState("authors")
  const [token, setToken] = useState(localStorage.getItem("library-user-token"))
  const [notification, setNotification] = useState(null)

  const logout = () => {
    setToken(null)
    localStorage.clear()
  }

  console.log("token on App", token)

  return (
    <div>
      <Notification notification={notification} />
      <div>
        {useSubscription(BOOK_ADDED, {
          onSubscriptionData: ({ subscriptionData }) => {
            console.log(subscriptionData)
          }
        })}

        {/* <Subscription subscription={BOOK_ADDED}>
          {(data, loading) => {
            console.log("loading...", data.data)
            if (!data.loading && data.data.bookAdded) {
              console.log("data ready", data)
              setNotification(data.data.bookAdded.title)
            }
            return null
          }}
        </Subscription> */}
      </div>
      <div>
        <button onClick={() => setPage("authors")}>authors</button>
        <button onClick={() => setPage("books")}>books</button>
        {token ? (
          <React.Fragment>
            <button onClick={() => setPage("add")}>add book</button>
            <button onClick={logout}>log out</button>
          </React.Fragment>
        ) : (
          <button onClick={() => setPage("login")}>log in</button>
        )}
      </div>

      <Authors show={page === "authors"} />

      <Query query={ME}>
        {me => <Books show={page === "books"} me={me} />}
      </Query>

      <Mutation mutation={LOGIN}>
        {login => (
          <Login
            show={page === "login"}
            login={login}
            setToken={token => setToken(token)}
          />
        )}
      </Mutation>

      <Mutation
        mutation={CREATE_BOOK}
        refetchQueries={[{ query: ALL_AUTHORS }, { query: ALL_BOOKS }]}
      >
        {addBook => <NewBook show={page === "add"} addBook={addBook} />}
      </Mutation>
    </div>
  )
}

export default App
