import React from "react"
import { Query } from "react-apollo"
import { gql } from "apollo-boost"

const Books = props => {
  if (!props.show) {
    return null
  }

  const ALL_BOOKS = gql`
    {
      allBooks {
        title
        author
        published
      }
    }
  `
  const books = []

  return (
    <div>
      <h2>Books</h2>
      <Query query={ALL_BOOKS}>
        {result => {
          if (result.loading) {
            return <div>loading...</div>
          }
          return (
            <table>
              <tbody>
                <tr>
                  <th />
                  <th>author</th>
                  <th>published</th>
                </tr>
                {result.data.allBooks.map(a => (
                  <tr key={a.title}>
                    <td>{a.title}</td>
                    <td>{a.author}</td>
                    <td>{a.published}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )
        }}
      </Query>
    </div>
  )
}

export default Books
